/*  _____ _______         _                      _
 * |_   _|__   __|       | |                    | |
 *   | |    | |_ __   ___| |___      _____  _ __| | __  ___ ____
 *   | |    | | '_ \ / _ \ __\ \ /\ / / _ \| '__| |/ / / __|_  /
 *  _| |_   | | | | |  __/ |_ \ V  V / (_) | |  |   < | (__ / /
 * |_____|  |_|_| |_|\___|\__| \_/\_/ \___/|_|  |_|\_(_)___/___|
 *                                _
 *              ___ ___ ___ _____|_|_ _ _____
 *             | . |  _| -_|     | | | |     |  LICENCE
 *             |  _|_| |___|_|_|_|_|___|_|_|_|
 *             |_|
 *
 *   PROGRAMOVÁNÍ  <>  DESIGN  <>  PRÁCE/PODNIKÁNÍ  <>  HW A SW
 *
 * Tento zdrojový kód je součástí výukových seriálů na
 * IT sociální síti WWW.ITNETWORK.CZ
 *
 * Kód spadá pod licenci prémiového obsahu a vznikl díky podpoře
 * našich členů. Je určen pouze pro osobní užití a nesmí být šířen.
 * Více informací na http://www.itnetwork.cz/licence
 */

import React, {useEffect, useState} from "react";
import {useParams} from "react-router-dom";
import {Link} from "react-router-dom";

import {apiGet} from "../utils/api";
import Country from "./Country";
import SalesAndPurchasesTable from "../invoices/SalesAndPurchasesTable";

const PersonDetail = () => {
    const {id} = useParams();
    const {identificationNumber} = useParams();
    const [person, setPerson] = useState({});
    const [purchases, setPurchases] = useState({});
    const [sales, setSales] = useState({});
    const [statistics, setStatistics] = useState([]);
   

    useEffect(() => {
        apiGet("/api/persons/" + id).then((data) => setPerson(data));
       
       
        apiGet("/api/persons/statistics").then((data) => {   
            console.log("Statistics:", data);        
            const personStatistics = data.find((item) => item.personId === parseInt(id));
            setStatistics(personStatistics);
        });  
    }, [id]);

    useEffect(() => {
        async function fetchSales () {
            try {
                const salesData = await apiGet(`/api/identification/${identificationNumber}/sales`);
                setSales(salesData);
                console.log("SalesData:", salesData);
            } catch (error) {
                console.error("Error fetching sales:", error);
            }
        };
    
        async function fetchPurchases () {
            try {
                const purchasesData = await apiGet(`/api/identification/${identificationNumber}/purchases`);
                setPurchases(purchasesData);
                console.log("PurchasesData:", purchasesData);
            } catch (error) {
                console.error("Error fetching purchases:", error);
            }
        };
    
        fetchSales();
        fetchPurchases();
    }, [identificationNumber]);
    if(!sales) {
        return <p>Nacitam</p>
    };
    {JSON.stringify(purchases)}
            {JSON.stringify(sales)}
    const country = Country.CZECHIA === person.country ? "Česká republika" : "Slovensko";

    return (
        <>
            <div>
                <h1>Detail osoby</h1>
                <hr/>
                <h3>{person.name} ({person.identificationNumber})</h3>
                <p>
                    <strong>DIČ:</strong>
                    <br/>
                    {person.taxNumber}
                </p>
                <p>
                    <strong>Bankovní účet:</strong>
                    <br/>
                    {person.accountNumber}/{person.bankCode} ({person.iban})
                </p>
                <p>
                    <strong>Tel.:</strong>
                    <br/>
                    {person.telephone}
                </p>
                <p>
                    <strong>Mail:</strong>
                    <br/>
                    {person.mail}
                </p>
                <p>
                    <strong>Sídlo:</strong>
                    <br/>
                    {person.street}, {person.city},
                    {person.zip}, {country}
                </p>
                <p>
                    <strong>Poznámka:</strong>
                    <br/>
                    {person.note}
                </p>
                
                <p>
                    <strong>Trzba:</strong> 
                    <br/>
                    {statistics.revenue} Kc
                </p>
                              
                              
            </div>
            
            <button className = "btn btn-primary">
                                <Link
                                    to={"/persons"}
                                    className = "btn btn-primary"
                                >
                                    zpet
                                </Link>
                                </button>
        </>
    );
};

export default PersonDetail;
