import React, {useEffect, useState} from "react";
import InvoiceTable from "./InvoiceTable";
import { apiDelete, apiGet } from "../utils/api";
import InvoiceFilter from "./InvoiceFilter";

const InvoiceIndex = () => {
    const[invoices, setInvoices] = useState([]);
    const [statistics, setStatistics] = useState(null);
    const [filterState, setFilter] = useState({
        seller: {_id: undefined} ,
        buyer: {_id:undefined},
        limit: undefined,
        product: undefined,
        minPrice: undefined,
        maxPrice: undefined,
        limit: undefined
    });
    const handleChange = (e) => {
        // pokud vybereme prázdnou hodnotu (máme definováno jako true/false/'' v komponentách), nastavíme na undefined
        if (e.target.value === "false" || e.target.value === "true" || e.target.value === '') {
            setFilter(prevState => {
                return {...prevState, [e.target.name]: undefined}
            });
        } else {
            setFilter(prevState => {
                return { ...prevState, [e.target.name]: e.target.value}
            });
        }
    };

    const deleteInvoice = async (id) => {
        try {
            await apiDelete("/api/invoices/" + id); 
            setInvoices(prevInvoices => prevInvoices.filter(item => item._id !== id)); // Aktualizace stavu faktur po smazání
        } catch (error) {
            console.log(error.message);
            alert(error.message);
        }
    };

    useEffect(() => {
        apiGet("/api/invoices").then((data) => setInvoices(data));
        apiGet("/api/invoices/statistics").then((data) => setStatistics(data));
       
    }, []);

    return (
        <div>
            
            <h1>Seznam faktur</h1>
            {statistics && (
                <>
            <p>Suma za aktualni rok: {statistics.currentYearSum} Kc </p>
            <p>Suma celkove: {statistics.allTimeSum} Kc</p>            
            </>
            )}
            <InvoiceTable
                deleteInvoice={deleteInvoice}
                items={invoices}
                label={"Pocet faktur"}
                />
              
        </div>
    );
};
export default InvoiceIndex;
