import React, {useEffect, useState} from "react";
import {useNavigate, useParams} from "react-router-dom";

import {apiGet, apiPost, apiPut} from "../utils/api";

import InputField from "../components/InputField";
import InputCheck from "../components/InputCheck";
import FlashMessage from "../components/FlashMessage";
import InputSelect from "../components/InputSelect";



const InvoiceForm = () => {
    const navigate = useNavigate();
    const { id } = useParams();
    const [invoice, setInvoice] = useState({
      invoiceNumber: "",
      buyer: null,
      seller: null,
      price: "",
      vat: "",
      note: "",
      issued: "",
      dueDate: "",
      product: ""
    });
  
    const [sentState, setSent] = useState(false);
    const [persons, setPersons] = useState([]);
    const [successState, setSuccess] = useState(false);
    const [errorState, setError] = useState(null);
  
    useEffect(() => {
      const fetchInvoices = async () => {
        try {
          const dataInvoices = await apiGet("/api/invoices/" + id);
          setInvoice(dataInvoices.results);
        } catch (error) {
          setError(error.message);
        }
      };
      if (id) {
        fetchInvoices();
      }
    }, [id]);
  
    useEffect(() => {
      const fetchPersons = async () => {
        try {
          const dataPersons = await apiGet("/api/persons");
          setPersons(dataPersons.results);
        } catch (error) {
          setError(error.message);
        }
      };
      fetchPersons();
    }, []);
  
    const handleSubmit = async (e) => {
      e.preventDefault();
      if (!invoice.buyer || !invoice.seller) {
        return;
      }
  
      try {
        const dataInvoices = await (id
          ? apiPut("/api/invoices/" + id, invoice)
          : apiPost("/api/invoices", invoice));
        setSent(true);
        setSuccess(true);
        navigate("/invoices");
      } catch (error) {
        console.log(error.message);
        setError(error.message);
        setSent(true);
        setSuccess(false);
      }
    };
  
    const handleChangeBuyer = (selectedBuyerId) => {
      const selectedBuyer = persons.find((person) => person._id === selectedBuyerId);
      setInvoice({ ...invoice, buyer: selectedBuyer });
    };
  
    const handleChangeSeller = (selectedSellerId) => {
      const selectedSeller = persons.find((person) => person._id === selectedSellerId);
      setInvoice({ ...invoice, seller: selectedSeller });
    };
  
    return (
      <div>
        <h1>{id ? "Upravit" : "Vytvorit"} fakturu</h1>
        <hr />
        {errorState && <div className="alert alert-danger">{errorState}</div>}
        {sentState && (
          <FlashMessage
            theme={successState ? "success" : ""}
            text={successState ? "Uložení osobnosti proběhlo úspěšně." : ""}
          />
        )}
  
        <form onSubmit={handleSubmit}>
          <InputField
            required={true}
            type="text"
            name="invoiceNumber"
            min="3"
            label="Cislo faktury"
            prompt="Zadejte cislo faktury"
            value={invoice.invoiceNumber}
            handleChange={(e) => {
              setInvoice({ ...invoice, invoiceNumber: e.target.value });
            }}
          />
  
  {persons.length > 0 && (
  <InputSelect
    required={true}
    name="buyerId"
    label="Nakupci"
    multiple={false}
    value={invoice.buyer ? invoice.buyer._id : ""}
    objectItems={persons.map((person) => ({
      value: person._id,
      label: person.name
    }))}
    handleChange={handleChangeBuyer}
  />
)}

{persons.length > 0 && (
  <InputSelect
    required={true}
    name="sellerId"
    label="Prodavajici"
    multiple={false}
    value={invoice.seller ? invoice.seller._id : ""}
    objectItems={persons.map((person) => ({
      value: person._id,
      label: person.name
    }))}
    handleChange={handleChangeSeller}
  />
)}
  
          <InputField
            required={true}
            type="number"
            name="price"
            min="3"
            label="Castka"
            prompt="Zadejte castku"
            value={invoice.price}
            handleChange={(e) => {
              setInvoice({ ...invoice, price: e.target.value });
            }}
          />
          <InputField
            required={true}
            type="number"
            name="vat"
            min="1"
            label="VAT"
            prompt="Zadejte DPH sazbu"
            value={invoice.vat}
            handleChange={(e) => {
              setInvoice({ ...invoice, vat: e.target.value });
            }}
          />
          <InputField
            required={false}
            type="text"
            name="note"
            min="0"
            label="Poznamka"
            prompt="Zadejte poznamku"
            value={invoice.note}
            handleChange={(e) => {
              setInvoice({ ...invoice, note: e.target.value });
            }}
          />
          <InputField
            required={true}
            type="date"
            name="issued"
            min="3"
            label="Datum vystaveni faktury"
            prompt="Zadejte datum vystaveni faktury"
            value={invoice.issued}
            handleChange={(e) => {
              setInvoice({ ...invoice, issued: e.target.value });
            }}
          />
          <InputField
            required={true}
            type="date"
            name="dueDate"
            min="3"
            label="Datum splatnosti faktury"
            prompt="Zadejte datum splatnosti faktury"
            value={invoice.dueDate}
            handleChange={(e) => {
              setInvoice({ ...invoice, dueDate: e.target.value });
            }}
          />
  
          <InputField
            required={true}
            type="text"
            name="product"
            min="3"
            label="Produkt"
            prompt="Zadejte ucel fakturace"
            value={invoice.product}
            handleChange={(e) => {
              setInvoice({ ...invoice, product: e.target.value });
            }}
          />
  
          <input type="submit" className="btn btn-primary" value="Uložit" />
        </form>
      </div>
    );
  };
  
  export default InvoiceForm;
