import React, {useEffect, useState} from "react";
import {useNavigate, useParams} from "react-router-dom";

import {apiGet, apiPost, apiPut} from "../utils/api";

import InputField from "../components/InputField";
import InputCheck from "../components/InputCheck";
import FlashMessage from "../components/FlashMessage";


const InvoiceForm = () => {
    const navigate = useNavigate();
    const {id} = useParams();
    const [invoice, setInvoice] = useState({
        invoice_number: "",
        issued: "",
        due_date: "",
        note: "",
        price: "",
        vat: "",
        buyer_id: "",
        seller_id: "",

    });

    useEffect(() => {
        if (id) {
            apiGet("/api/invoices/" + id).then((data) => setInvoice(data));
        }
    }, [id]);

    const handleSubmit = (e) => {
        e.preventDefault();

        (id ? apiPut("/api/invoices/" + id, invoice) : apiPost("/api/invoices", invoice))
            .then(() => {
                navigate("/invoices");
            })
            .catch((error) => {
                console.log(error.message);
            });
    };

    return (
        <div>
            <h1>{id ? "Upravit" : "Vytvorit"} fakturu</h1>
            <hr/>
           
            <form onSubmit={handleSubmit}>
                <InputField
                required={true}
                type="text"
                name="invoice_number"
                min="3"
                label="Cislo faktury"
                prompt="Zadejte cislo faktury"
                value={invoice.invoice_number}
                handleChange = {(e) => {
                    setInvoice({...invoice, invoice_number: e.target.value});
                }}
                />
                <InputField
                required={true}
                type="number"
                name="buyer_id"
                min="1"
                label="Kupujici"
                prompt="Zadejte ID nakupujiciho"
                value={invoice.buyer_id}
                handleChange = {(e) => {
                    setInvoice({...invoice, buyer: e.target.value});
                }}
                />
                <InputField
                required={true}
                type="number"
                name="seller"
                min="1"
                label="Prodavajici"
                prompt="Zadejte ID prodavajiciho"
                value={invoice.seller}
                handleChange = {(e) => {
                    setInvoice({...invoice, seller: e.target.value});
                }}
                />
                <InputField
                required={true}
                type="number"
                name="price"
                min="3"
                label="Castka"
                prompt="Zadejte castku"
                value={invoice.price}
                handleChange = {(e) => {
                    setInvoice({...invoice, price: e.target.value});
                }}
                />
                <InputField
                required={true}
                type="number"
                name="vat"
                min="1"
                label="VAT"
                prompt="Zadejte DPH sazbu"
                value={invoice.vat}
                handleChange = {(e) => {
                    setInvoice({...invoice, vat: e.target.value});
                }}
                />
                <InputField
                required={false}
                type="text"
                name="note"
                min="0"
                label="Poznamka"
                prompt="Zadejte poznamku"
                value={invoice.note}
                handleChange = {(e) => {
                    setInvoice({...invoice, note: e.target.value});
                }}
                />
                <InputField
                required={true}
                type="date"
                name="issued"
                min="3"
                label="Datum vystaveni faktury"
                prompt="Zadejte datum vystaveni faktury"
                value={invoice.issued}
                handleChange = {(e) => {
                    setInvoice({...invoice, issued: e.target.value});
                }}
                />
                <InputField
                required={true}
                type="date"
                name="dueDate"
                min="3"
                label="Datum splatnosti faktury"
                prompt="Zadejte datum splatnosti faktury"
                value={invoice.dueDate}
                handleChange = {(e) => {
                    setInvoice({...invoice, dueDate: e.target.value});
                }}
                />

                
                <input type="submit" className="btn btn-primary" value="UloÅ¾it"/>
                
            </form>
        </div>
    );

};

export default InvoiceForm;
