import React, {useEffect, useState} from "react";
import {useNavigate, useParams} from "react-router-dom";

import {apiGet, apiPost, apiPut} from "../utils/api";

import InputField from "../components/InputField";
import InputCheck from "../components/InputCheck";
import FlashMessage from "../components/FlashMessage";
import InputSelect from "../components/InputSelect";


const InvoiceForm = () => {
    const navigate = useNavigate();
    const {id} = useParams();
    const [invoice, setInvoice] = useState({
        invoiceNumber: "",
        issued: "",
        dueDate: "",
        note: "",
        price: "",
        vat: "",
        buyer: "",
        seller: "",

    });


    const [personListState, setPersonList] = useState([]);
    const [sentState, setSent] = useState(false);
    const [personState, setPersonState] = useS
    const [successState, setSuccess] = useState(false);
    const [errorState, setError] = useState(null);


    useEffect(() => {
        if (id) {
            apiGet("/api/invoices" + id).then((data) => setInvoice(data));
        }

        apiGet ('/api/persons').then((data) => setPersonList(data));
    }, [id]);

    

    const handleSubmit = (e) => {
        e.preventDefault();

        (id ? apiPut("/api/invoices/" + id, invoice) : apiPost("/api/invoices", invoice))
            .then((data) => {
                setSent(true);
                setSuccess(true);
                navigate("/invoices");
            })
            .catch((error) => {
                console.log(error.message);
                setError(error.message);
                setSent(true);
                setSuccess(false);
            });
    };

    const sent = sentState;
    const success = successState;

    return (
        <div>
            <h1>{id ? "Upravit" : "Vytvorit"} fakturu</h1>
            <hr/>
            {errorState ? (
                <div className="alert alert-danger">{errorState}</div>
            ) : null}
            {sent && (
                <FlashMessage
                    theme={success ? "success" : ""}
                    text={success ? "Uložení osobnosti proběhlo úspěšně." : ""}
                />
            )}
           
            <form onSubmit={handleSubmit}>
                <InputField
                required={true}
                type="text"
                name="invoiceNumber"
                min="3"
                label="Cislo faktury"
                prompt="Zadejte cislo faktury"
                value={invoice.invoiceNumber}
                handleChange = {(e) => {
                    setInvoice({...invoice, invoiceNumber: e.target.value});
                }}
                />
                
                <InputSelect
                required={true}
                name="buyer"
                label="Nakupci"
                multiple={false}                              
                value={invoice.buyer}
                items={personListState}
                handleChange = {(e) => {
                    setInvoice({...invoice, buyer: e.target.value});
                }}
                
                    
                />
                
                <InputSelect
                required={true}
                name="seller"
                label="Prodavajici"
                multiple={false}                              
                value={invoice.seller}
                items={personListState}
                handleChange = {(e) => {
                    setInvoice({...invoice, seller: e.target.value});
                }}
                
                    
                />
                <InputField
                required={true}
                type="number"
                name="price"
                min="3"
                label="Castka"
                prompt="Zadejte castku"
                value={invoice.price}
                handleChange = {(e) => {
                    setInvoice({...invoice, price: e.target.value});
                }}
                />
                <InputField
                required={true}
                type="number"
                name="vat"
                min="1"
                label="VAT"
                prompt="Zadejte DPH sazbu"
                value={invoice.vat}
                handleChange = {(e) => {
                    setInvoice({...invoice, vat: e.target.value});
                }}
                />
                <InputField
                required={false}
                type="text"
                name="note"
                min="0"
                label="Poznamka"
                prompt="Zadejte poznamku"
                value={invoice.note}
                handleChange = {(e) => {
                    setInvoice({...invoice, note: e.target.value});
                }}
                />
                <InputField
                required={true}
                type="date"
                name="issued"
                min="3"
                label="Datum vystaveni faktury"
                prompt="Zadejte datum vystaveni faktury"
                value={invoice.issued}
                handleChange = {(e) => {
                    setInvoice({...invoice, issued: e.target.value});
                }}
                />
                <InputField
                required={true}
                type="date"
                name="dueDate"
                min="3"
                label="Datum splatnosti faktury"
                prompt="Zadejte datum splatnosti faktury"
                value={invoice.dueDate}
                handleChange = {(e) => {
                    setInvoice({...invoice, dueDate: e.target.value});
                }}
                />

                
                <input type="submit" className="btn btn-primary" value="Uložit"/>
                
            </form>
        </div>
    );

};

export default InvoiceForm;
